mapper.update:
update notification
set noti_date = #{notiDate,jdbcType=TIMESTAMP},
  noti_summarized = #{notiSummarized,jdbcType=VARCHAR},
  noti_title = #{notiTitle,jdbcType=VARCHAR},
  noti_content = #{notiContent,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

update notification
set noti_date = #{notiDate,jdbcType=TIMESTAMP},
  noti_summarized = #{notiSummarized,jdbcType=VARCHAR},
  noti_title = #{notiTitle,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select
n.id, noti_date, noti_summarized, noti_title
from notification as n inner join stu_notification as sn
                              on sn.student_id = #{stuId,jdbcType=INTEGER}
                              and n.id = sn.notification_id;
                              limit #{offset},#{limit}
