mapper.select:
select 

from score
where id = #{id,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.delete:
delete from score
where id = #{id,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.update:
update score

  
    score = #{score,jdbcType=INTEGER},
  

where id = #{id,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

update score
set score = #{score,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.select:
select
cin.course_name,s.score,cin.course_credit
from score as s inner join course_info as cin
                      on s.course_id=cin.id
                      and cin.course_year=#{year,jdbcType=VARCHAR}
                      and s.student_id=#{stuId,jdbcType=INTEGER}

  select id from student where major_id =
                                  (
                                   select major_id from student where id=#{stuId,jdbcType=INTEGER}
                                  )
