mapper.select:
select 

from video
where id = #{id,jdbcType=INTEGER}
  and video_series_id = #{videoSeriesId,jdbcType=INTEGER}

mapper.delete:
delete from video
where id = #{id,jdbcType=INTEGER}
  and video_series_id = #{videoSeriesId,jdbcType=INTEGER}

mapper.update:
update video

  
    video_path = #{videoPath,jdbcType=VARCHAR},
  
  
    video_title = #{videoTitle,jdbcType=VARCHAR},
  

where id = #{id,jdbcType=INTEGER}
  and video_series_id = #{videoSeriesId,jdbcType=INTEGER}

update video
set video_path = #{videoPath,jdbcType=VARCHAR},
  video_title = #{videoTitle,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and video_series_id = #{videoSeriesId,jdbcType=INTEGER}
