mapper.select:
select

from elective_comments
where id = #{id,jdbcType=INTEGER}
and elective_id = #{electiveId,jdbcType=INTEGER}
and student_id = #{studentId,jdbcType=INTEGER}

mapper.delete:
delete from elective_comments
where id = #{id,jdbcType=INTEGER}
  and elective_id = #{electiveId,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}

mapper.update:
update elective_comments

  
    comment_content = #{commentContent,jdbcType=VARCHAR},
  
  
    comment_time = #{commentTime,jdbcType=TIMESTAMP},
  

where id = #{id,jdbcType=INTEGER}
and elective_id = #{electiveId,jdbcType=INTEGER}
and student_id = #{studentId,jdbcType=INTEGER}

update elective_comments
set comment_content = #{commentContent,jdbcType=VARCHAR},
  comment_time = #{commentTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
  and elective_id = #{electiveId,jdbcType=INTEGER}
  and student_id = #{studentId,jdbcType=INTEGER}

mapper.select:
select
  ec.student_id,s.avatar_path,ec.comment_time,ec.comment_content
from elective_comments as ec inner join student as s
                                    on ec.elective_id=#{id,jdbcType=INTEGER}
                                    and ec.student_id=s.id;
